 *			   DateEnd: int (as a year),
 *			   ImageURL: string,
 *			   ArtistHref: string,
 * 			 }
 *
 * selectedAlbums[] where
 * 	Album = {  AlbumId: int,
 *			   AlbumName: string,
 *			   AlbumType: string,
 *			   ReleaseDate: int (as a year),
 *			   AlbumHref: string,
 * 			 }
 *
 * selectedTracks[] where
 * 	Track = {  TrackId: int,
