		return (
			<Container>
		        <Row>
		       	  <Col>
		       	  	<img className="w2 h2 w3-ns h3-ns br-100" src="http://tachyons.io/img/avatar-mrmrs.jpg"/>
		          </Col>
		          <Col>ArtistName</Col>
		        </Row>
		        <Row>
		          <Col>DateStart</Col>
		          <Col>DateEnd</Col>
		        </Row>
		        <Row>Selected Albums:
		        </Row>
		        <Row>Selected Tracks:
		        </Row>
		    </Container>
		);
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(BioPane);
